import React from 'react'
import {useState} from 'react'

export const ReusableInput =()=> {
    const[value,setValue]=useState(ReusableInput)
    const handleChanges=(e)=>{
        setValue(e.target.value)
    }
    const reset=()=>{
        setValue("");
    }
  return [value,handleChanges, reset]
}
