import axios from 'axios';
import React, {useState} from 'react'
import '../generalStyle.css'

function Signin() {

    const[username, setUsername]=useState('');
    const[email, setEmail]=useState('');
    const[password,setPassword]=useState('');
    const[rptpassword,setRptpassword]=useState('');
    const[errorPassword, setErrorpassword]=useState(0);

    const handleSingin=async(e)=>{
        e.preventDefault();

        const config={
          Headers:{
            "Content-type": "application/json"
          }
        }

        if(password===rptpassword){
            try {

              const {data}=axios.post("http://localhost:9000/api/users",
              {username,password,email},config);

              console.log(data)

            } catch (error) {
              console.log(error)
            }
        }else{
            setErrorpassword(1);
        }
    }

  return (
    <div className="container vh-100">
      <div className="row d-flex justify-content-center">
        <div className="card col-12 col-md-4 m-4">
          <div className="card-body">
            <h5 className="card-title">Fill the form to Sing up!</h5>
            <form onSubmit={handleSingin} className='d-flex flex-column'>
              <label htmlFor="username">Username</label>
              <input 
                type="text" 
                id="username" 
                value={username}
                onChange={(e)=>setUsername(e.target.value)} />

              <label htmlFor="email">Email:</label>
              <input 
                type="email" 
                id="email" 
                value={email}
                onChange={(e)=>setEmail(e.target.value)} />

              <label htmlFor="pssword">Password:</label>
              <input 
                type="password" 
                id="pssword" 
                value={password}
                onChange={(e)=>setPassword(e.target.value)} 
                minLength="10"/>

              <label htmlFor="repeatpsswrd">Repeat Password:</label>
              <input 
                type="password" 
                id="repeatpsswrd" 
                value={rptpassword}
                minLength="10"
                onChange={(e)=>setRptpassword(e.target.value)}/>

              <br/>
              <button className="btn btn-success" type="submit">Sign up!</button>
              {
                errorPassword===1 ? 
                <h4 className='text-danger'>Las contrase√±as no son iguales</h4> 
                :
                <div></div>
              }
            </form>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Signin
