import React,{useState,useEffect} from 'react'

function Tabla() {

  const[allpokemon,setPokemon]=useState([]);

  const prueba=allpokemon.map((pokemon)=>{
    `<td>this is a pokemon</td>
    <td>this is a pokemon</td>
    <td>this is a pokemon</td>`
  } );


  useEffect(()=>{
    const pokemon = async () =>{
      const data= await fetch("https://pokeapi.co/api/v2/pokemon/");
      const res= await data.json();

      const pokemones= res.results;
      setPokemon(pokemones);
    } 
    pokemon();
  },[]);

  return (
    <div className="col-12 col-md-6">
        <h2>Hola desde el componente de la tabla</h2>
        <table className="table">
          <thead>
            <tr>
              <th>id_pokemon</th>
              <th>Nombre</th>
              <th>Tipo</th>
            </tr>
          </thead>
          <tbody>
               <tr>
                    {prueba}
                </tr>
          </tbody>
        </table>
    </div>
  )
}

export default Tabla
