import React,{useState,useEffect} from 'react'
import axios from 'axios';
import tablatipos from '../tabladetipos.json'

function Stadistics() {

    const[userpokemon, setUserpokemon]=useState([]);

    const electricStyle= {
      backgroundColor:'#f9e79f'
    }

    const genericStyle= {
      backgroundColor:'blue'
    }

    useEffect(()=>{
        const bringPokemonuser=async()=>{

            try {
                const id_user=(JSON.parse(localStorage.getItem("userInfo")))._id;
                const {data}= await axios.get(`http://localhost:9000/api/pokemon/${id_user}`)
                setUserpokemon(data);
                console.log(data);
            } catch (error) {
                console.log(error)
            }
        }
        bringPokemonuser();
        //console.log(tablatipos);
    },[])


  return (
    <div className='row'>
      <h3>Your team is mostly "Type"</h3>
      <div className="d-flex flex-wrap justify-content-center">
      {
        userpokemon.length>0 ?
        userpokemon.map((pokemon,index)=>(
            <div className="card col12 col-md-3" 
                 key={index}
                 style={pokemon.typesPokemon[0]==='electric' ? electricStyle : genericStyle}>
                <div className="card-body">
                    <h5 className="card-title">{pokemon.name}</h5>
                    <h6 className="card-subtitle mb-2 text-muted">
                      {
                        pokemon.typesPokemon.map(tipo=>('-'+tipo))
                      }
                    </h6>
                    <p className="card-text">{pokemon.gender}</p>
                    <h6>Strengths</h6>
                    <h6>Weaknesses</h6>
                    <h6>No efective</h6>
                    <h6>Immune</h6>
                </div>
            </div>
        ))
        :
        <h3>You dont have pokemon on your team yet!</h3>
      }
      </div>
    </div>
  );
}

export default Stadistics
